import { Card } from '@material-ui/core'
import React, { Component } from 'react'
import { loginUser } from '../Services/bookstore';
import "./login.css";




export class LoginPage extends Component {

  constructor(props) {
    super(props);
    this.state = {

        email: '',
        password: ''

    }
}

handleEmail = (event) => {
    this.setState({
      email: event.target.value
    })
}


handlePassword= (event) => {
    this.setState({
      password: event.target.value
    })
}

handleLogin = async (e) => {
  let data = {
    email : this.state.email,
    password : this.state.password,
  };

  await loginUser(data).then((res) => {
    let message = "";
    // if(res.data.statusCode === 200){
    // message = "Sign in successfully"
    console.log("response" + res.data);
    localStorage.setItem('token', res.data.message);
    console.log("token", + localStorage.getItem('token'));
  })

//   await axios.get("http://localhost:8080/User/login", this.state).then((response)=>{
//       console.log(response.data)}).catch((error)=>{

//      console.log(error.response.data.message);
//   })
//   localStorage.setItem('token')
//   alert("Login Successfully")
 }


  render() {
    return (
      <div className='form'>
        <Card className='card'>
        <h1 className='login'>Login to Bookstore</h1>
        <div>
        <label className='emp-name-field' > Email </label><input onChange={(event)=>this.handleEmail(event)} value={this.state.email}></input>
        <div>
        <label className='emp-name-field' > Password </label><input onChange={(event)=>this.handlePassword(event)} value={this.state.password}></input></div>
        <div>
        <h5>Forgot Password</h5><button className='emp-name-field' onClick={this.handleLogin}> Login </button></div>
        </div>
        </Card>
      </div>
    )
  }
}
