import React, { Component } from 'react';
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import CardActions from '@mui/material/CardActions';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import TextField from '@mui/material/TextField';
import RadioGroup from '@mui/material/RadioGroup';
import FormControlLabel from '@mui/material/FormControlLabel';
import Radio from '@mui/material/Radio';
import Checkbox from '@mui/material/Checkbox';
import Select from '@mui/material/Select';
import MenuItem from '@mui/material/MenuItem';
import TextareaAutosize from '@mui/material/TextareaAutosize';
import Avatar from '@mui/material/Avatar';
import './EmployeeCard.css';
import img1 from './icon.png';


class EmployeeCard extends Component {

    constructor(props) {
        super(props);
        this.state = {
            employeeName: '',
            profile_image: '',
            gender: '',
            department: '',
            salary: '',
            note: ''

        }
    }

    handleEmpName = (event) => {
        this.setState({
            employeeName: event.target.value
        })
    }

    handleProfile = (event) => {
        this.setState({
            profile_image: event.target.value
        })
    }

    handleGender = (event) => {
        this.setState({
            gender: event.target.value
        })
    }

    handleDepartment = (event) => {
        this.setState({
            department: event.target.value
        })
    }

    handleSalary = (event) => {
        this.setState({
            salary: event.target.value
        })
    }

    handleNote = (event) => {
        this.setState({
            note: event.target.value
        })
    }



    /*    handleEmp_Name = (event) => {
            console.log(event.target.value);
            let fnameError = 'Please enter your name'
            let fname = event.target.value
            let regx = RegExp("^[A-Z]{1}[A-Za-z]{2,}$")
            if (regx.test(fname)) {
                this.setState({
                    emp_name: fname,
                    empNameError: ""
                })
            }
            else {
                this.setState({
                    emp_name: fname,
                    empNameError: "please enter proper name"
                });
            }
            console.log(this.emp_name);
        }


        valid = () => {
            if (this.state.emp_name.length === 0) {
                this.setState({ nameError: "Enter Valid Name" })
            }
        }
    */
    handleSubmit = async (e) => {

        console.log(this.state);
        await axios.post("http://localhost:8080/register", this.state)

    }
    // notifyCancel = () => {
    //     toast('Cancel Form');

    // }

    // notifySubmit = () => {
    //     toast('Employee Added!!');
    // }
    render() {
        return(
            <Card className='form_card' variant="outlined">
                <CardContent>
                <Typography className="title" variant="h5" component="div">
                    Employee Payroll Form
                </Typography>
                <div className='field_container'><div className='form_label' onChange={(event)=>this.handleEmployeeName} value={this.state.employeeName} >Name</div> <TextField variant="outlined"  size="small"/><p>{this.state.employeeName}</p></div> 
                <div className='field_container'><div className='form_label'>Profile Image</div> 
                    <RadioGroup
                        row
                        aria-labelledby="demo-row-radio-buttons-group-label"
                        name="row-radio-buttons-group"
                    >

                        <FormControlLabel value="female" control={<Radio />} label={<Avatar className='profile_img' src={img1} />} />
                        <FormControlLabel value="male" control={<Radio />} label={<Avatar className='profile_img' src={img1}/>} />
                        <FormControlLabel value="gender" control={<Radio />} label={<Avatar className='profile_img' src={img1}/>} />
                        <FormControlLabel value="other" control={<Radio />} label={<Avatar className='profile_img' src={img1}/>} />
                    </RadioGroup>
                </div>
                <div className='field_container'><div className='form_label'>Gender</div> 
                    <RadioGroup
                        row
                        aria-labelledby="demo-row-radio-buttons-group-label"
                        name="row-radio-buttons-group"
                    >
                        <FormControlLabel value="male" control={<Radio />} label="Male" />
                        <FormControlLabel value="female" control={<Radio />} label="Female" />
                    </RadioGroup>
                </div>
                <div className='field_container'><div className='form_label'>Department</div> 
                    <div>
                        <Checkbox /> HR
                        <Checkbox /> Sales
                        <Checkbox /> Finance
                        <Checkbox /> Engineer
                        <Checkbox /> Others
                    </div>
                </div>
                <div className='field_container'><div className='form_label'>Salary</div> 
                <div>
                <Select className='emp-salary' value={this.state.salary} onChange={(event) => this.handleSalary(event)}>
                        <MenuItem value={30000}>30000</MenuItem>
                        <MenuItem value={40000}>40000</MenuItem>
                        <MenuItem value={50000}>50000</MenuItem>
                    </Select>
                </div>
                </div>

                <div className='field_container'><div className='form_label'>Start Date</div> 
                <div>
                    <Select
                    labelId="demo-simple-select-label"
                    id="demo-simple-select"
                    value={1}
                    label="startDate"
                    >
                        <MenuItem value={1}>20</MenuItem>

                    </Select>
                    <Select
                    labelId="demo-simple-select-label"
                    id="demo-simple-select"
                    value={2}
                    label="startDate"
                    >
                        <MenuItem value={2}>12</MenuItem>

                    </Select>
                    <Select
                    labelId="demo-simple-select-label"
                    id="demo-simple-select"
                    value={3}
                    label="startDate"
                    >
                        <MenuItem value={3}>2022</MenuItem>

                    </Select>
                </div>
                </div>

                <div className='field_container'><div className='form_label'>Notes</div> 
                    <div>
                    <TextareaAutosize
                        minRows={4}
                        aria-label="maximum height"
                        style={{ width: 500 }}
                    />

                    </div>
                </div>

                </CardContent>
                <CardActions className="action_container">
                    <div><Button variant="outlined" className="action_button">Cancel</Button></div>
                    <div>
                        <Button variant="outlined" className="action_button" onClick={this.handleSubmit}>Submit</Button>
                        <Button variant="outlined" type="reset" className="action_button" onClick={this.reset}>Reset</Button>
                    </div>
                </CardActions>
            </Card>  
        )
    }
}

export default EmployeeCard;
