import React, { Component } from 'react'
import "./login.css";


export class LoginPage extends Component {

  constructor(props) {
    super(props);
    this.state = {

        email: '',
        password: ''

    }
}

handleEmail = (event) => {
    this.setState({
      email: event.target.value
    })
}


handlePassword= (event) => {
    this.setState({
      password: event.target.value
    })
}

handleLogin = async (e) => {

  // console.log(this.state);

  await axios.post("http://localhost:8080/User/login", this.state).then((response)=>{
      console.log(response.data)}).catch((error)=>{

     console.log(error.response.data.message);
  })

  alert("Login Successfully")
}


  render() {
    return (
      <div className='form'>
        <Card className='card'>
        <h1 className='login'>Login</h1>
        <div>
        <label className='emp-name-field' > Email </label><input onChange={(event)=>this.handleEmail(event)} value={this.state.email}></input>
        <div>
        <label className='emp-name-field' > Password </label><input></input></div>
        <div>
        <h5>Forgot Password</h5><button className='emp-name-field' onClick={this.handleSubmit}> Login </button></div>
        </div>
        </Card>
      </div>
    )
  }
}
